import csv
baseline=[]
with open('mutationbaseline.csv', mode ='r') as file: 
    baseline = list(csv.reader(file))

    reference=""
    pairingprob=0
    omega=0
    total=0
    for i in range(0, len(reference)):
        if reference[i]=="c":
            total+=1
            pairingprob+=float(baseline[i][3])
            omega+=float(baseline[i][2])

print(pairingprob/total, omega/total)
